package net.uku3lig.uklient.util;

import com.google.gson.JsonElement;
import com.google.gson.JsonObject;
import net.uku3lig.uklient.download.RequestManager;
import net.uku3lig.uklient.model.LauncherProfile;

import java.io.*;
import java.nio.charset.StandardCharsets;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.time.Instant;
import java.util.Locale;

public class MinecraftHelper {
    public static Path findMcDir() {
        String os = System.getProperty("os.name").toLowerCase(Locale.ENGLISH);
        Path dir;

        if (os.contains("win") && System.getenv("APPDATA") != null) {
            dir = Paths.get(System.getenv("APPDATA")).resolve(".minecraft");
        } else {
            String home = System.getProperty("user.home", ".");
            Path homeDir = Paths.get(home);

            if (os.contains("mac")) {
                dir = homeDir.resolve("Library").resolve("Application Support").resolve("minecraft");
            } else {
                dir = homeDir.resolve(".minecraft"); // linux B)
            }
        }

        return dir.toAbsolutePath().normalize();
    }

    public static void createLauncherProfile(Path mcDir, Path installDir, String fabricVersion, String mcVersion) {
        LauncherProfile profile = new LauncherProfile();
        Instant now = Instant.now();

        profile.setCreated(now);
        profile.setLastPlayed(now);
        profile.setIcon(ICON);
        profile.setLastVersionId(String.format("fabric-loader-%s-%s", fabricVersion, mcVersion));
        profile.setName("uklient for " + mcVersion);
        profile.setType("custom");
        if (!installDir.equals(mcDir)) profile.setGameDir(installDir.normalize().toAbsolutePath().toString());

        Path p = mcDir.resolve("launcher_profiles.json").normalize().toAbsolutePath();
        JsonElement jsonProfile = RequestManager.getGson().toJsonTree(profile);

        try (FileReader reader = new FileReader(p.toFile())) {
            JsonObject root = RequestManager.getGson().fromJson(reader, JsonObject.class);
            root.getAsJsonObject("profiles").add("uklient+"+mcVersion, jsonProfile);

            Writer writer = new BufferedWriter(new OutputStreamWriter(new FileOutputStream(p.toFile()), StandardCharsets.UTF_8));
            RequestManager.getGson().toJson(root, writer);

            writer.flush();
            writer.close();
        } catch (IOException e) {
            System.err.println("Could not create launcher profile. Please try again later.");
            System.exit(1);
        }
    }

    public static void copyUserFiles(Path mcPath, Path uklientPath) {
        Path mcOptions = mcPath.resolve("options.txt");
        Path mcPacks = mcPath.resolve("resourcepacks");
        Path mcServers = mcPath.resolve("servers.dat");

        Path uklientOptions = uklientPath.resolve("options.txt");
        Path uklientPacks = uklientPath.resolve("resourcepacks");
        Path uklientServers = uklientPath.resolve("servers.dat");

        try {
            Files.createDirectories(uklientPacks);
            Files.walkFileTree(mcPacks, ResourceManager.getVisitor(mcPacks, uklientPacks));

            if (!Files.exists(uklientOptions)) Files.copy(mcOptions, uklientOptions);
            if (!Files.exists(uklientServers)) Files.copy(mcServers, uklientServers);
        } catch (IOException e) {
            System.err.println("Could not copy resourcepacks, options and servers. Please try again later.");
            System.exit(1);
        }
    }

    public static final String ICON = " data:image/png;base64,";

    private MinecraftHelper() {}
}
